          jQuery('.list h5').click(function() {

              // removing active class for active element
              jQuery('a.active').removeClass('active');

              if (jQuery(this).parent().next('.mobile-show').is(':visible')) {
                  jQuery(this).parent().next('.mobile-show').slideUp();
                  jQuery(this).parent().next().next('.mobile-show').slideUp();
                  jQuery(this).removeClass('active');
              } 
              else {
                  jQuery(this).parent().next('.mobile-show').slideDown();
                  jQuery(this).parent().next().next('.mobile-show').slideDown();
                  jQuery(this).addClass('active');
              }
          });

          function validateEmails(string) {
             var regex = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/;
             var result = string.replace(/\s/g, "").split(/,|;/);        
             for(var i = 0;i < result.length;i++) {
                 if(!regex.test(result[i])) {
                     return false;
                 }
             }       
             return true;
         }
          jQuery('#send_invitation').click(function(){
              var refer_email = jQuery("#refer_email").val();

              if(refer_email != ''){
                   if(validateEmails(refer_email)){
                        jQuery('#refer_message').text('');
                        jQuery.ajax({
                              type : "POST",
                              dataType : "json",
                              url : "<?php echo home_url(); ?>/wp-admin/admin-ajax.php?action=refer_email_submit",
                              data : {refer_email : refer_email},
                              success: function(response) {
                                   if(response.status == 1){
                                        jQuery('#refer_email').val('');
                                        jQuery('#refer_message').text(response.message);
                                        setTimeout(function(){ $("#refer_message").html(''); }, 5000);
                                   }
                                   else{
                                        jQuery('#refer_message').text(response.message);
                                        setTimeout(function(){ $("#refer_message").html(''); }, 5000);
                                   }
                              }
                         })
                    }
                    else{
                         jQuery("#refer_message").text('Please enter valid Email ID');
                         setTimeout(function(){ $("#refer_message").html(''); }, 5000);
                    }
               }else{
                    jQuery("#refer_message").text('Please enter Email ID');
                    setTimeout(function(){ $("#refer_message").html(''); }, 5000);
               }
          });
